import logo from './logo.svg';
import image1 from './images/bg.jpg';
import React, { useState } from 'react';
import './css/style.css';
import 'popper.js';
import './js/popper';
import { FiEye } from 'react-icons/fi';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [rememberMe, setRememberMe] = useState(true);

  function handleSubmit(e) {
    e.preventDefault();
    // Add code to handle login form submission
  }

  function togglePasswordVisibility() {
    const passwordField = document.getElementById('password-field');
    if (passwordField.type === 'password') {
      passwordField.type = 'text';
    } else {
      passwordField.type = 'password';
    }
  }

  return (
<div className="img js-fullheight" style={{backgroundImage: `url(${image1})`, backgroundSize: 'cover', height: '100vh', opacity: 1.8}}>
      <section className="ftco-section">
        <div className="container">
          <div className="row justify-content-center">
            <div className="col-md-6 text-center mb-5">
              <h2 className="heading-section">Login #10</h2>
            </div>
          </div>
          <div className="row justify-content-center">
            <div className="col-md-6 col-lg-4">
              <div className="login-wrap p-0">
                <h3 className="mb-4 text-center">Have an account?</h3>
                <form onSubmit={handleSubmit} className="signin-form">
                  <div className="form-group">
                    <input
                      type="text"
                      className="form-control"
                      placeholder="Username"
                      required
                      value={username}
                      onChange={(e) => setUsername(e.target.value)}
                    />
                  </div>
                  <div className="form-group">
  <input
    id="password-field"
    type="password"
    className="form-control"
    placeholder="Password"
    required
    value={password}
    onChange={(e) => setPassword(e.target.value)}
  />
  <span
    onClick={togglePasswordVisibility}
    className="field-icon toggle-password"
  >
    <FiEye />
  </span>
</div>
                  <div className="form-group">
                    <button type="submit" className="form-control btn btn-primary submit px-3">
                      Sign In
                    </button>
                  </div>
                  <div className="form-group d-md-flex">
                    <div className="w-50">
                      <label className="checkbox-wrap checkbox-primary">
                        Remember Me
                        <input
                          type="checkbox"
                          checked={rememberMe}
                          onChange={(e) => setRememberMe(e.target.checked)}
                        />
                        <span className="checkmark"></span>
                      </label>
                    </div>
                    <div className="w-50 text-md-right">
                      <a href="#" style={{ color: '#fff' }}>
                        Forgot Password
                      </a>
                    </div>
                  </div>
                </form>                
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
  );

}

export default Login;
